				layout_flexGrow: 1,
				layout_flexBasisPercent: type
			}
	});
$$=(a,b,c)=> `/type/${a}_${b}_0_${c}_0_{{page}\}.html`;


a=b=c=0;
d=(title,a,b,c)=> push("༺ˇ»`ʚ "+title+" ɞ´«ˇ༻",$$(a,b,c),1);

arra=["综合","修真","仙侠","情感","历史","网游","穿越","恐怖","其他"];
arrb=[["综合","0"],["阅读","allvisit"],["推荐","allvote"],["收藏","goodnum"],["更新","lastupdate"],["新书","postdate"]];
arrc=(title,a,b)=>["["+title+"]","连载","全本"]
		.map((title,c)=>push(title,$$(a,b,c),0.25));


if(style==0){
		arrb.map([title,b]=>{
				d(title,a,b,c);
				arra.map((title,a)=>arrc(title,a,b));
			});
	}
else{
		arra.map((title,a)=>{
				d(title,a,b,c);
				arrb.map([title,b]=>arrc(title,a,b));
			});
	}
JSON.stringify(sort);

